<!DOCTYPE html><html><script        src="https://code.jquery.com/jquery-3.2.1.min.js"        integrity="sha256-hwg4gsxgFZhOsEEamdOYGBf13FyQuiTwlAQgxVSNgt4="        crossorigin="anonymous"></script><body><div id="map" style="width:50%;height:300px"></div><br><form action="/map" method="post">    {{ csrf_field() }}    <input name="lat" type="text" id="lat" style="display: none">    <input name="lng" type="text" id="lng" style="display: none">    <button type="submit">submit</button></form><script>    function myMap() {        var myCenter = new google.maps.LatLng(37.062914807102906,50.423115491867065);        var mapCanvas = document.getElementById("map");        var mapOptions = {center: myCenter, zoom: 12};        var map = new google.maps.Map(mapCanvas, mapOptions);        $('#addMarker').click(function () {            $('#addMarker').hide();            $('#deleteMarker').show();            google.maps.event.addListener(map, 'click', function(event) {                placeMarker(event.latLng);                google.maps.event.clearListeners(map, 'click');            });        });        $('#deleteMarker').click(function () {            $('#deleteMarker').hide();            $('#addMarker').show();            $('#lat').val('');            $('#lng').val('');            marker.setMap(null);        });        function placeMarker(location) {            $('#lat').val(location.lat());            $('#lng').val(location.lng());            marker = new google.maps.Marker({                position: location,                map: map            });        }    }</script><br><br><button id="addMarker">Add Marker</button><button id="deleteMarker" style="display: none">Delete Marker</button><script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBJXj14jwwsgwA1DeIRMY5jBiRwT_byxVs&callback=myMap"></script></body></html>